mapper.select:
SELECT USER_ID, USER_ENROLL_DATE
FROM MEMBER
WHERE USER_NAME =
#{userName}
AND USER_BIRTH = #{userBirth}
AND USER_PHONE = #{userPhone}
AND USER_STATUS = 'Y'

SELECT
COUNT(*)
FROM MEMBER
WHERE USER_ID = #{userId}
AND USER_NAME =
#{userName}
AND USER_PWD_A = #{userPwdA}
AND USER_STATUS = 'Y'

mapper.update:
UPDATE MEMBER
SET USER_PWD =
#{userPwd}
WHERE USER_ID = #{userId}

mapper.select:
SELECT
COUNT(*)
FROM MEMBER
WHERE USER_ID = #{userId}
AND USER_PWD = #{userPwd}

SELECT *
FROM ATTACHMENT
WHERE USER_NO = #{userNo}

SELECT
COUNT(*)
FROM AUTH
WHERE EMAIL = #{userEmail}
AND AUTH_NUM = #{authNum}

mapper.delete:
DELETE REVIEW
WHERE
USER_NO = #{userNo}
AND R_SORT = 'M'
AND MOVIE_NO = #{movieNo}

DELETE REVIEW
WHERE
USER_NO = #{userNo}
AND MOVIE_NO = #{movieNo}
AND R_SORT = 'V'
